namespace authall\platforms;

class Wexin extends Base
{
    private $code;

    public function init()
    {
//        parent::init(); // TODO: Change the autogenerated stub
    }

    /**
     * @return mixed
     */
    public function getCode()
    {
        return $this->code;
    }

    /**
     * @param $code
     * @throws \Exception
     */
    public function setCode($code)
    {
        if (empty($code)){
            throw new \Exception('code not by empty');
        }

        $this->setParams(['code' => $code]);
    }



}